(242, '18a220dc5e5cb5370336e41b5d173b4e', 'EX2-10\r\nСОЗДАТЬ РАЗДЕЛ /EX2/TIME_CONTROL/\nПЕРЕЙТИ НА СТРАНИЦУ НАСТРОЙКИ > ПРОИЗВОДИТЕЛЬНОСТЬ > ПАНЕЛЬ ПРОИЗВОДИТЕЛЬНОСТИ\nНАЖАТЬ НА КНОПКУ ТЕСТ ПРОИЗВОДИТЕЛЬНОСТИ ЗАПУСТИТЬ ХОТЯ БЫ НА 1 МИН\nВО ВРЕМЯ ТЕСТА НУЖНО ОТКРЫТЬ ВСЕ ССЫЛКИ ИЗ ВЕРХНЕГО МЕНЮ ПО НЕСКОЛЬКО РАЗ.\nВЫПИСАТЬ ИЗ РЕЗУЛЬТАТА САМУЮ ДОЛГУЮ СТРАНИЦУ, УКАЗАВ ЕЕ ДОЛЮ % В ОБЩЕЙ СТАТИСТИКЕ НАГРУЗКИ.\n<?REQUIRE($_SERVER[\"DOCUMENT_ROOT\"].\"/BITRIX/HEADER.PHP\"); \r$APPLICATION->SETTITLE(\"ОЦЕНКА ПРОИЗВОДИТЕЛЬНОСТИ 2\"); \r?><H2>НАГРУЗКА</H2> \rСТРАНИЦА: \r/PRODUCTS/INDEX.PHP\">HTTP://BITRIX-EX2-TTALL/BITRIX/ADMIN/PERFMON_HIT_LIST.PHP?LANG=RU&SET_FILTER=Y&FIND_SCRIPT_NAME=%2FB...\rДОЛЯ: 20.53%<BR> \r<H2>САМЫЙ НАГРУЖАЕМЫЙ КОМПОНЕНТ</H2> \rНАЗВАНИЕ: BITRIX:CATALOG<BR> \rВРЕМЯ: 0.0773 С<BR><?REQUIRE($_SERVER[\"DOCUMENT_ROOT\"].\"/BITRIX/FOOTER.PHP\");?>\r\n'),
(243, 'e947286853b411e1b6fe00f55d056855', 'EX2-11\r\nСОЗДАТЬ РАЗДЕЛ /EX2/TIME_CONTROL/\nПЕРЕЙТИ НА СТРАНИЦУ НАСТРОЙКИ > ПРОИЗВОДИТЕЛЬНОСТЬ > ПАНЕЛЬ ПРОИЗВОДИТЕЛЬНОСТИ\nНАЖАТЬ НА КНОПКУ ТЕСТ ПРОИЗВОДИТЕЛЬНОСТИ ЗАПУСТИТЬ ХОТЯ БЫ НА 1 МИН\nВО ВРЕМЯ ТЕСТА НУЖНО ОТКРЫТЬ ВСЕ ССЫЛКИ ИЗ ВЕРХНЕГО МЕНЮ ПО НЕСКОЛЬКО РАЗ.\nВЫПИСАТЬ ИЗ РЕЗУЛЬТАТА САМУЮ ДОЛГУЮ СТРАНИЦУ, УКАЗАВ ЕЕ ДОЛЮ % В ОБЩЕЙ СТАТИСТИКЕ НАГРУЗКИ.\nREQUIRE($_SERVER[\"DOCUMENT_ROOT\"].\"/BITRIX/HEADER.PHP\"); \r$APPLICATION->SETTITLE(\"ОЦЕНКА ПРОИЗВОДИТЕЛЬНОСТИ 3\"); \r?><H2>НАГРУЗКА</H2> \rСТРАНИЦА: \r/BITRIX/URLREWRITE.PHP\">HTTP://BITRIX-EX2-TTALL/BITRIX/ADMIN/PERFMON_HIT_LIST.PHP?LANG=RU&SET_FILTER=Y&FIND_SCRIPT_NAME=%2FB...\rДОЛЯ: 37.19%<?REQUIRE($_SERVER[\"DOCUMENT_ROOT\"].\"/BITRIX/FOOTER.PHP\");?>\r\n'),
