(245, '5e5e079e9ce989e462a7e2a5ba3bee43', 'EX2-34\r\nКОПИРУЕМ ШАБЛОН FURNITURE_PALE-BLUE В ПАПКУ LOCAL.\nВ /WWW/LOCAL/TEMPLATES/FURNITURE_PALE-BLUE/HEADER.PHP В ТЕГ HEAD ДОБАВЛЯЕМ:\n<META PROPERTY=\"SPECIALDATE\" CONTENT=\"<?$APPLICATION->SHOWPROPERTY(\"SPECIALDATE\")?>\" /> \rНАХОДЯСЬ НА КОРНЕВОЙ СТРАНИЦЕ САЙТА, НАЖИМАЕМ – ИЗМЕНИТЬ РАЗДЕЛ -> СВОЙСТВА РАЗДЕЛА. В ВЫПАВШЕМ ОКОШКЕ НАЖИМАЕМ «РЕДАКТИРОВАТЬ СВОЙСТВА ПАПКИ В ПАНЕЛИ УПРАВЛЕНИЯ». ПОПАДАЕМ В СВОЙСТВА ПАПКИ АДМ. РАЗДЕЛА.\nДОБАВЛЯЕМ КОД SPECIALDATE СО ЗНАЧЕНИЕМ – 100.\nИЗ ПАПКИ /WWW/BITRIX/COMPONENTS/BITRIX/NEWS/TEMPLATES/ КОПИРУЕМ В ПАПКУ /LOCAL/TEMPLATES/FURNITURE_PALE-BLUE/COMPONENTS/BITRIX/NEWS/ ПАПКУ .DEFAULT. \rВ .PARAMETERS.PHP, В МАССИВ $ARTEMPLATEPARAMETERS ДОБАВЛЯЕМ:\n(ПОСЛЕ КЛЮЧА \"DISPLAY_PREVIEW_TEXT\")\n\"SPECIALDATE\" => ARRAY( \r \"NAME\" => GETMESSAGE(\"SPECIALDATE_META_TAG\"), \r \"TYPE\" => \"CHECKBOX\", \r \"DEFAULT\" => \"Y\", \r),\rВ ФАЙЛЕ .PARAMETERS.PHP ПАПКИ LANG/RU ДОБАВЛЯЕМ ПАРАМЕТР:\n$MESS[\"SPECIALDATE_META_TAG\"] = \"УСТАНОВИТЬ СВОЙСТВО СТРАНИЦЫ SPECIALDATE\";\nВ ФАЙЛ NEWS.PHP В МАССИВ ВЫЗОВА КОМПОНЕНТА BITRIX:NEWS.LIST ДОБАВЛЯЕМ:\n\"SPECIALDATE\" => $ARPARAMS[\"SPECIALDATE\"]\n(ПОСЛЕ КЛЮЧА \"CHECK_DATES\" => $ARPARAMS[\"CHECK_DATES\"].\nВ ШАБЛОНЕ КОМПОНЕНТА NEWS.LIST (/WWW/LOCAL/TEMPLATES/FURNITURE_PALE-BLUE/COMPONENTS/BITRIX/NEWS/.DEFAULT/BITRIX/NEWS.LIST/.DEFAULT/)\nСОЗДАЕМ ДВА ФАЙЛА: COMPONENT_EPILOG.PHP И RESULT_MODIFIER.PHP.\nВ RESULT_MODIFIER.PHP:\n<?IF(!DEFINED(\"B_PROLOG_INCLUDED\") || B_PROLOG_INCLUDED!==TRUE)DIE();\rIF($ARPARAMS[\"SPECIALDATE\"] == \"Y\")\r{\r $ARRESULT[\'SPECIALDATE\'] = $ARRESULT[\'ITEMS\'][0][\'ACTIVE_FROM\']; \r $THIS->GETCOMPONENT()->SETRESULTCACHEKEYS(ARRAY(\'SPECIALDATE\'));\r}\rВ COMPONENT_EPILOG.PHP:\nIF($ARPARAMS[\'SPECIALDATE\'] == \'Y\'){ \r $APPLICATION->SETPAGEPROPERTY(\"SPECIALDATE\", $ARRESULT[\'SPECIALDATE\']); \r} \rДАЛЕЕ РАЗМЕЩАЕМ КОМПОНЕНТ НОВОСТЕЙ С ДЕФОЛТНЫМ ШАБЛОНОМ \rДЛЯ ПРОВЕРКИ.\r\n'),
(246, '7a15b9df562a1563cc1888ea5558225f', 'EX2-49\r\n[EX2-49] ДОБАВИТЬ ДОПОЛНИТЕЛЬНУЮ ФИЛЬТРАЦИЮ ЭЛЕМЕНТОВ В СОЗДАННЫЙ ПРОСТОЙ КОМПОНЕНТ «КАТАЛОГ ТОВАРОВ» \rЕСЛИ ЗАДАНИЕ [EX2-70]:\rЕСЛИ ЗАДАНИЕ [EX2-71]:\r\n'),
(247, '151a02a1f078fbfd5ae69d0917d63a02', 'EX2-50\r\n[EX2-50] ПРОВЕРКА ПРИ ДЕАКТИВАЦИИ ТОВАРА \rВ ПАПКЕ LOCAL СОЗДАЕМ ПАПКУ PHP_INTERFACE И В НЕЙ ФАЙЛ INIT.PHP С КОДОМ: \rIF(FILE_EXISTS($_SERVER[\"DOCUMENT_ROOT\"].\"/LOCAL/PHP_INTERFACE/INCLUDE/CONSTANTS.PHP\"))\r REQUIRE_ONCE($_SERVER[\"DOCUMENT_ROOT\"].\"/LOCAL/PHP_INTERFACE/INCLUDE/CONSTANTS.PHP\");\rIF(FILE_EXISTS($_SERVER[\"DOCUMENT_ROOT\"].\"/LOCAL/PHP_INTERFACE/INCLUDE/HANDLERS.PHP\"))\r REQUIRE_ONCE($_SERVER[\"DOCUMENT_ROOT\"].\"/LOCAL/PHP_INTERFACE/INCLUDE/HANDLERS.PHP\");\rВ ПОДКЛЮЧАЕМЫХ ФАЙЛАХ РАСПОЛАГАЕМ КОД, В HANDLERS.PHP: \r<?\rUSE \\BITRIX\\MAIN\\LOCALIZATION\\LOC;\rLOC::LOADMESSAGES(__FILE__);\rADDEVENTHANDLER(\"IBLOCK\", \"ONBEFOREIBLOCKELEMENTUPDATE\", ARRAY(\"EXAMHANDLERS\", \"ONBEFOREIBLOCKELEMENTUPDATEHANDLER\"));\rCLASS EXAMHANDLERS\r{\r FUNCTION ONBEFOREIBLOCKELEMENTUPDATEHANDLER(&$ARFIELDS)\r {\r IF(($ARFIELDS[\"ACTIVE\"] == \"N\") && ($ARFIELDS[\"IBLOCK_ID\"] == CATALOG_ID)){\r $R = CIBLOCKELEMENT::GETBYID($ARFIELDS[\'ID\']);\r IF($RES = $R -> FETCH()){\r $SHOW_COUNTER = $RES[\'SHOW_COUNTER\'];\r }\r IF($SHOW_COUNTER > SHOW_COUNTER_VALUE){\r GLOBAL $APPLICATION;\r $APPLICATION->THROWEXCEPTION(LOC::GETMESSAGE(\'SHOW_COUNTER_ERROR_1\').$SHOW_COUNTER.LOC::GETMESSAGE(\'SHOW_COUNTER_ERROR_2\'));\r RETURN FALSE;\r }\r }\r }\r}\rВ CONSTANTS.PHP: \r<? \r DEFINE(\"NEWS_ID\", 1);\r DEFINE(\"CATALOG_ID\", 2);\r \r \rDEFINE(\"SHOW_COUNTER_VALUE\", 2);\r?> \rСОЗДАДИМ ПАПКУ LANG/RU С ФАЙЛОМ DEACTIVAT.PHP: \r<? \r $MESS[\"SHOW_COUNTER_ERROR_1\"] = \"ТОВАР НЕВОЗМОЖНО ДЕАКТИВИРОВАТЬ, У НЕГО[\"; \r $MESS[\"SHOW_COUNTER_ERROR_2\"] = \"] ПРОСМОТРОВ\"; \r?>\r\n'),
(248, '40b1e138f66a26f34dcdb3b32c4fddf0', 'EX2-51\r\n[EX2-51] ИЗМЕНЕНИЕ ДАННЫХ В ПИСЬМЕ \rВ /LOCAL/PHP_INTERFACE/INCLUDE/HANDLERS.PHP РАЗМЕЩАЕМ:\n<?\rUSE \\BITRIX\\MAIN\\LOCALIZATION\\LOC;\rLOC::LOADMESSAGES(__FILE__);\rADDEVENTHANDLER(\"MAIN\", \"ONBEFOREEVENTADD\", ARRAY(\"EXAMHANDLERS\", \"ONBEFOREEVENTADDHANDLER\"));\rCLASS EXAMHANDLERS\r{\r FUNCTION ONBEFOREEVENTADDHANDLER(&$EVENT, &$LID, &$ARFIELDS){\r IF($EVENT == \'FEEDBACK_FORM\'){\r GLOBAL $USER;\r IF($USER->ISAUTHORIZED()){\r $ARFIELDS[\'AUTHOR\'] = LOC::GETMESSAGE(\'AUTHORIZE_USER_1\').\"[\".$USER->GETID().\"] (\".$USER->GETLOGIN().\") \".$USER->GETFULLNAME().\r LOC::GETMESSAGE(\'FORM_DATA\').$ARFIELDS[\'AUTHOR\'];\r }\r ELSE{\r $ARFIELDS[\'AUTHOR\'] = LOC::GETMESSAGE(\'NO_AUTHORIZE\').$ARFIELDS[\'AUTHOR\'];\r }\r CEVENTLOG::ADD(ARRAY(\r \"SEVERITY\" => \"SECURITY\",\r \"AUDIT_TYPE_ID\" => LOC::GETMESSAGE(\'ZAMENA\'),\r \"MODULE_ID\" => \"MAIN\",\r \"ITEM_ID\" => $EVENT,\r \"DESCRIPTION\" => LOC::GETMESSAGE(\'ZAMENA\').$ARFIELDS[\'AUTHOR\'] ,\r )\r );\r }\r }\r} \rВ ЛЕНГОВОМ ФАЙЛЕ:\n$MESS[\"AUTHORIZE_USER_1\"] = \"ПОЛЬЗОВАТЕЛЬ АВТОРИЗОВАН:\"; \r$MESS[\"FORM_DATA\"] = \" ДАННЫЕ ИЗ ФОРМЫ: \"; \r$MESS[\"NO_AUTHORIZE\"] = \"ПОЛЬЗОВАТЕЛЬ НЕ АВТОРИЗОВАН, ДАННЫЕ ИЗ ФОРМЫ: \"; \r$MESS[\"ZAMENA\"] = \"ЗАМЕНА ДАННЫХ В ОТСЫЛАЕМОМ ПИСЬМЕ\"; \rРАЗМЕЩЕННАЯ ФОРМА ОБРАТНОЙ СВЯЗИ ДЛЯ ПРОВЕРКИ\r\n'),
(249, '5ce51a30d5d124d8059a1a4d44618cda', 'EX2-54\r\n[EX2-54] ПОДСЧЕТ КОЛИЧЕСТВА ЗАРЕГИСТРИРОВАННЫХ ПОЛЬЗОВАТЕЛЕ \rСОЗДАЕМ АГЕНТА С ПАРАМЕТРАМИ: \rВ INIT.PHP ДОБАВИМ: \rIF(FILE_EXISTS($_SERVER[\"DOCUMENT_ROOT\"].\"/LOCAL/PHP_INTERFACE/INCLUDE/AGENTS.PHP\"))\r REQUIRE_ONCE($_SERVER[\"DOCUMENT_ROOT\"].\"/LOCAL/PHP_INTERFACE/INCLUDE/AGENTS.PHP\");\rВ AGENTS.PHP ПИШЕМ ФУНКЦИЮ:\nFUNCTION CHECKUSERCOUNT() \r{ \r $DATE = NEW DATETIME(); \r \r//ВЫВОД, НАПРИМЕР: 21.01.2018 10:20:26\r $DATE = \\BITRIX\\MAIN\\TYPE\\DATETIME::CREATEFROMTIMESTAMP($DATE->GETTIMESTAMP());\r \r//ПОСЛЕДНЕЕ ВЫПОЛНЕНИЕ АГЕНТА \r $LASTDATE = COPTION::GETOPTIONSTRING(\"MAIN\", \'LAST_DATE_AGENT_CHECKUSERCOUNT\');\r IF ($LASTDATE) { \r $ARFILTER = ARRAY(\'>=DATE_REGISTER\' => $LASTDATE); \r } ELSE { \r $ARFILTER = ARRAY(); \r } \r $RSUSERS = CUSER::GETLIST($BY = \"DATE_REGISTER\", $ORDER = \"ASC\", $ARFILTER); \r $ARUSERS = ARRAY(); \r WHILE ($USER = $RSUSERS->FETCH()) { \r $ARUSERS[] = $USER; \r } \r IF (! $LASTDATE) { \r $LASTDATE = $ARUSERS[0][\'DATE_REGISTER\']; \r } \r $DIFFERENCE = INTVAL(ABS( \r STRTOTIME($LASTDATE) - STRTOTIME($DATE->TOSTRING()) \r )); \r $DAYS = ROUND($DIFFERENCE / (3600 * 24)); \r $COUNTUSERS = COUNT($ARUSERS); \r $RSADMINS = CUSER::GETLIST($BY = \"ID\", $ORDER = \"ASC\", ARRAY(\'GROUPS_ID\' => 1)); \r WHILE ($ADMIN = $RSADMINS->FETCH()) { \r CEVENT::SEND(\'COUNT_REGISTERED_USERS\', \'S1\', ARRAY( \r \'EMAIL_TO\' => $ADMIN[\'EMAIL\'], \r \'COUNT_USERS\' => $COUNTUSERS, \r \'COUNT_DAYS\' => $DAYS, \r ), \"Y\", \"29\"); \r } \r COPTION::SETOPTIONSTRING(\"MAIN\", \'LAST_DATE_AGENT_CHECKUSERCOUNT\', $DATE->TOSTRING()); \r RETURN \"CHECKUSERCOUNT();\"; \r}\r\n'),
(250, '15c6c9500c41395078a130e23365e1ac', 'EX2-58\r\n[EX2-58] ДОБАВИТЬ УПРАВЛЕНИЕ ЭЛЕМЕНТАМИ – «ЭРМИТАЖ» В СОЗДАННЫЙ ПРОСТОЙ КОМПОНЕНТ \"КАТАЛОГ ТОВАРОВ\" \rЕСЛИ ЗАДАНИЕ [EX2-70] И ЭРМИТАЖ ДОБАВЛЕН ДЛЯ ЭЛЕМЕНТОВ ИБ \"НОВОСТИ\":\rЕСЛИ ЗАДАНИЕ [EX2-71] И ЭРМИТАЖ ДОБАВЛЕН ДЛЯ ЭЛЕМЕНТОВ ИБ \"ФИРМА - ПРОИЗВОДИТЕЛЬ\":\r\n'),
(251, '2878c785411498244e305418b11df93d', 'EX2-60\r\n[EX2-60] ДОБАВИТЬ ПОСТРАНИЧНУЮ НАВИГАЦИЮ В СОЗДАННЫЙ ПРОСТОЙ КОМПОНЕНТ \rДЛЯ ЗАДАНИЯ [EX2-71].\nВ .PARAMETERS.PHP ДОБАВЛЯЕМ: \r\"ELEMENTS_PER_PAGE\" => ARRAY( \r \r\"PARENT\" => \"BASE\", \r \"NAME\" => GETMESSAGE(\"ELEMENTS_PER_PAGE\"), \r \"TYPE\" => \"STRING\", \r \"DEFAULT\" => \"2\" \r), \rВ ЛЭНГОВЫЙ .PARAMETERS.PHP ДОБАВЛЯЕМ \r$MESS[\"ELEMENTS_PER_PAGE\"] = \"ЭЛЕМЕНТОВ НА СТРАНИЦЕ\"; \rОСТАЛЬНАЯ МАГИЯ В COMPONENT.PHP И TEMPLATE.PHP\rСОБСТВЕННО, САМ КОМПОНЕНТ:\r\n'),
(252, '72d50fef28f37702da0718715fc053f5', 'EX2-70\r\n[EX2-70] РАЗРАБОТАТЬ ПРОСТОЙ КОМПОНЕНТ «КАТАЛОГ ТОВАРОВ» \rКОПИРУЕМ ИЗ МАТЕРИАЛОВ ПРОСТОЙ КОМПОНЕНТ. \rВ АДМ. РАЗДЕЛЕ, КОНТЕНТ – ТОВАРЫ И УСЛУГИ – ПРОДУКЦИЯ, ЖМЁМ НА СЕНДВИЧ – ИЗМЕНИТЬ, ВКЛАДКА ДОП. ПОЛЯ - ДОБАВИТЬ ПОЛЬЗОВАТЕЛЬСКОЕ СВОЙСТВО. \rЛИБО ПРОСТО ДОБАВИТЬ ПОЛЬЗОВАТЕЛЬСКОЕ ПОЛЕ С ОБЪЕКТОМ - IBLOCK_2_SECTION. \rТИП: \rПРИВЯЗКА К ЭЛЕМЕНТАМ ИБ\r; \rКОД: \rUF_NEWS_LINK\r; \rМНОЖЕСТВЕННОЕ: \rДА\r; \rИНФОБЛОК: \rНОВОСТИ, НОВОСТИ\r; \rЯЗЫКОВЫЕ НАСТРОЙКИ: \rUF_NEWS_LINK\r. \rВЫБИРАЕМ ВСЕ РАЗДЕЛЫ ИБ ПРОДУКЦИЯ И УСТАНАВЛИВАЕМ КАК НА РИСУНКЕ:\nНАСТРАИВАЕМ ПАРАМЕТРЫ: \rID ИБ ТОВАРОВ – \r2\r; \rID ИБ НОВОСТЕЙ – \r1\r; \rКОД ПОЛЬЗОВАТЕЛЬСКОГО СВОЙСТВА РАЗДЕЛОВ КАТАЛОГА - \rUF_NEWS_LINK\r. \rСОБСТВЕННО, ВЫВОД КОМПОНЕНТА:\r\n'),
(253, '398ece97545e50f49d0f625418e7a878', 'EX2-71\r\n[EX2-71] РАЗРАБОТАТЬ ПРОСТОЙ КОМПОНЕНТ «КАТАЛОГ ТОВАРОВ» \rСОЗДАЕМ ИБ «\rФИРМА – ПРОИЗВОДИТЕЛЬ\r» (В ТИПЕ ИБ «ТОВАРЫ И УСЛУГИ») И В НЕМ ТРИ ЭЛЕМЕНТА. ЭЛЕМЕНТЫ НАЗЫВАЕМ ПРОИЗВОЛЬНЫМИ НАЗВАНИЯМИ ФИРМ, НАПРИМЕР: \rФИРМА1\r, \rФИРМА2\rИ ПР. \rВ ИБ «\rПРОДУКЦИЯ\r» СОЗДАЕМ СВОЙСТВО «\rФИРМА\r»; \rКОД: \rCOMPANY\r; \rТИП: \rПРИВЯЗКА К ЭЛЕМЕНТАМ\r; \rМНОЖЕСТВЕННОЕ: \rДА\r; \rИНФОРМАЦИОННЫЙ БЛОК: \rТОВАРЫ И УСЛУГИ, ФИРМА – ПРОИЗВОДИТЕЛЬ\r. \rВ ИНФОРМАЦИОННОМ БЛОКЕ \rПРОДУКЦИЯ\rЗАДАЕМ У НЕСКОЛЬКИХ ЭЛЕМЕНТОВ ЗНАЧЕНИЕ МНОЖЕСТВЕННОГО СВОЙСТВА ФИРМА, НЕ МЕНЕЕ ЧЕМ У 4Х ТОВАРОВ, ПРИВЯЗАВ КАЖДЫЙ ТОВАР К 2-3 ЭЛЕМЕНТАМ КЛАССИФИКАТОРА (ФИРМАМ).\nНАСТРАИВАЕМ ПАРАМЕТРЫ:\rID ИБ ТОВАРОВ – \r2\r; \rID ИБ ФИРМ – \r7\r; \rКОД ПОЛЬЗОВАТЕЛЬСКОГО СВОЙСТВА РАЗДЕЛОВ КАТАЛОГА - \rCOMPANY_MULTY\r.\nСОБСТВЕННО, ВЫВОД КОМПОНЕНТА:\r\n'),
(254, 'be151a951fa58ed2ac3e7738f91155a8', 'EX2-81\r\n[EX2-81] ВНЕСТИ ДОРАБОТКИ В СОЗДАННЫЙ ПРОСТОЙ КОМПОНЕНТ «КАТАЛОГ ТОВАРОВ» \r В .PARAMETERS.PHP ДОБАВЛЯЕМ ПУНКТ: \r\"TEMPLATE_DETAIL_URL\" => ARRAY( \r \"PARENT\" => \"BASE\", \r \"NAME\" => GETMESSAGE(\"TEMPLATE_DETAIL_URL\"), \r \"TYPE\" => \"STRING\", \r \"MULTIPLE\" => \"N\", \r \"DEFAULT\" => \"/CATALOG_EXAM/#SECTION_ID#/#ELEMENT_CODE#\", \r), \rВ /LANG/RU/.PARAMETERS.PHP ДОБАВЛЯЕМ: \r$MESS[\'TEMPLATE_DETAIL_URL\'] = \'ШАБЛОН ССЫЛКИ НА ДЕТАЛЬНЫЙ ПРОСМОТР\'; \rОСТАЛЬНАЯ МАГИЯ В COMPONENT.PHP И TEMPLATE.PHP.\rДЛЯ НАГЛЯДНОСТИ, МОЖНО В ИБ ПРОДУКЦИЯ: ТОВАРЫ ЗАДАТЬ СИМВОЛЬНЫЙ КОД.\rОТКРЫТЬ НАСТРОЙКИ ПАРАМЕТРОВ КОМПОНЕНТА И НАЖАТЬ СОХРАНИТЬ, ДЛЯ ПЕРЕСОХРАНЕНИЯ.\rВОТ И САМ КОМПОНЕНТ:\r\n'),
(255, '4c1a319fcb4284ffa1c3fcc7c479b203', 'EX2-82\r\n[EX2-82] ДОБАВИТЬ ОТОБРАЖЕНИЕ ДАННЫХ В ШАБЛОН САЙТА \rЭТО ЗАДАНИЕ СХОЖЕ С \rEX2-57\rЕСЛИ ЭТО КОМПОНЕНТ ИЗ ЗАДАНИЯ [EX2-70]: \rСОЗДАЕМ .../TEMPLATES/.DEFAULT/RESULT_MODIFIER.PHP С МАГИЕЙ.\rСОЗДАЕМ .../TEMPLATES/.DEFAULT/COMPONENT_EPILOG.PHP С МАГИЕЙ.\rВ /LOCAL/TEMPLATES/FURNITURE_PALE-BLUE/HEADER.PHP, ПЕРЕД \r<DIV CLASS=\"INFORMATION-BLOCK\"> ПИШЕМ: \r<?$APPLICATION->SHOWVIEWCONTENT(\'PRICES\');?>\rНУ И, САМ КОМПОНЕНТ:\r\n'),
(256, 'a3cd7dba8b32538a134061e4e42dacd0', 'EX2-88\r\n[EX2-88] ОЦЕНИТЬ СКОРОСТЬ РАБОТЫ САЙТА – СТРАНИЦЫ И СОЗДАННЫЙ ПРОСТОЙ КОМПОНЕНТ «КАТАЛОГ ТОВАРОВ» \rСОЗДАТЬ РАЗДЕЛ /EX2/TIME_CONTROL/ \rПЕРЕЙТИ НА СТРАНИЦУ НАСТРОЙКИ > ПРОИЗВОДИТЕЛЬНОСТЬ > ПАНЕЛЬ ПРОИЗВОДИТЕЛЬНОСТИ \rНАЖАТЬ НА КНОПКУ ТЕСТ ПРОИЗВОДИТЕЛЬНОСТИ ЗАПУСТИТЬ ХОТЯ БЫ НА 1 МИН \rВО ВРЕМЯ ТЕСТА НУЖНО ОТКРЫТЬ ВСЕ ССЫЛКИ ИЗ ВЕРХНЕГО МЕНЮ ПО НЕСКОЛЬКО РАЗ. \rВЫПИСАТЬ ИЗ РЕЗУЛЬТАТА САМУЮ ДОЛГУЮ СТРАНИЦУ, УКАЗАВ ЕЕ ДОЛЮ % В ОБЩЕЙ СТАТИСТИКЕ НАГРУЗКИ. \rДОБАВЛЯЙТЕ ИЛИ УДАЛЯЙТЕ ДАННЫЕ ДЛЯ КЕШИРОВАНИЯ В COMPONENT.PHP И СЛЕДИТЕ КАК МЕНЯЕТСЯ РАЗМЕР КЕША. \r \rОПРЕДЕЛЯЕМ ЧТО БУДЕТ КЕШИРОВАТЬСЯ \r $THIS->SETRESULTCACHEKEYS(ARRAY( \r \"PRODUCT_COUNT\", \r \"SECTIONS_COUNT\", \r \"NEWS_GROUP\", \r \"PRODUCT_GROUP_SECTION\", \r \"SET_TITLE\", \r )); \rЗАПИШИТЕ РЕЗУЛЬТАТЫ ДО И ПОСЛЕ. \rВ INDEX.PHP, НАПРИМЕР ТАК: \rREQUIRE($_SERVER[\"DOCUMENT_ROOT\"].\"/BITRIX/HEADER.PHP\"); \r$APPLICATION->SETTITLE(\"ОЦЕНКА ПРОИЗВОДИТЕЛЬНОСТИ\"); \r?><H2>НАГРУЗКА</H2> \rСТРАНИЦА: \r/SERVICES/INDEX.PHP\">\rHTTP://192.168.1.38/BITRIX/ADMIN/PERFMON_HIT_LIST.PHP?LANG=RU&SET_FILTER=Y&FIND_SCRIPT_NAME=%2FSERVI...\r.\r.\rДОЛЯ: 15.15%<BR> \r<H2>КЕШ</H2> \rПО УМОЛЧАНИЮ: <B>258 КБ</B><BR> \rПРИ ПОМЕЩЕНИИ В НЕГО ТОЛЬКО ДАННЫХ, НЕОБХОДИМЫХ В НЕКЕШИРУЕМОЙ ЧАСТИ: <B>245 КБ</B><BR> \r<?REQUIRE($_SERVER[\"DOCUMENT_ROOT\"].\"/BITRIX/FOOTER.PHP\");?>\r\n'),
(257, 'a5dccf444caae6107c6e9982dc036a24', 'EX2-94\r\n[EX2-94] СУПЕР ИНСТРУМЕНТ SEO СПЕЦИАЛИСТА \rСОГЛАСНО ЗАДАНИЮ: СОЗДАЕМ ИБ \r«МЕТАТЕГИ»,\rДОБАВЛЯЕМ СВОЙСТВА, СОЗДАЕМ ДВА ЭЛЕМЕНТА. \rОПРЕДЕЛЯЕМ КОНСТАНТУ IBLOCK_META В /LOCAL/PHP_INTERFACE/INCLUDE/CONSTANTS.PHP: \rDEFINE(\'IBLOCK_META\', 11);\r// В ДАННОМ СЛУЧАЕ \rВ /LOCAL/PHP_INTERFACE/INCLUDE/HANDLERS.PHP:\rADDEVENTHANDLER(\"MAIN\", \"ONBEFOREPROLOG\", ARRAY(\"EXAMHANDLERS\", \"MYONBEFOREPROLOGHANDLER\"), 50);\r\n'),
(258, 'dba0c61c9735a1ebb331c873f106b2d2', 'EX2-95\r\n[EX2-95] УПРОСТИТЬ МЕНЮ В АДМИНИСТРАТИВНОМ РАЗДЕЛЕ ДЛЯ КОНТЕНТ-МЕНЕДЖЕРА \rВ АДМ. РАЗДЕЛЕ ДОБАВЛЯЕМ ПОЛЬЗОВАТЕЛЯ С ЛОГИНОМ \rMANAGER\rИ ГРУППОЙ \rКОНТЕНТ-РЕДАКТОР\r. \rВ /LOCAL/PHP_INTERFACE/INCLUDE/HANDLERS.PHP ПИШЕМ ХЭНДЛЕР:\rADDEVENTHANDLER(\"MAIN\", \"ONBUILDGLOBALMENU\", ARRAY(\"EXAMHANDLERS\", \"ONBUILDGLOBALMENUHANDLER\"));\rНУ И ДАЛЬНЕЙШАЯ МАГИЯ В САМОМ ТЕЛЕ ФАЙЛА.\rПЕРЕХОДИМ В КОНТЕНТ -> ТИПЫ ИНФОБЛОКОВ, ЗАХОДИМ В ТИП ТОВАРЫ И УСЛУГИ: ИБ \rПРОДУКЦИЯ\r, \rУСЛУГИ\rИ \rВАКАНСИИ\r-> ДОСТУПЫ, ДЛЯ КОНТЕНТ-РЕДАКТОРА – \rПО УМОЛЧАНИЮ\r(ЛИБО - \rНЕТ ДОСТУПА\r).\r\n'),
(259, 'cda2eed01267428cac77e9cc67365957', 'EX2-97\r\n[EX2-97] РАЗРАБОТАТЬ ПРОСТОЙ КОМПОНЕНТ «НОВОСТИ ПО ИНТЕРЕСАМ» \r \rСОЗДАЕМ ПОЛЬЗОВАТЕЛЬСКОЕ ПОЛЕ: \rТИП: \rСПИСОК\r; \rОБЪЕКТ: \rUSER\r; \rКОД ПОЛЯ: \rUF_AUTHOR_TYPE\r; \rМНОЖЕСТВЕННОЕ: \rНЕТ\r; \rЯЗЫКОВЫЕ НАСТРОЙКИ: \rТИП АВТОРА\r. \rСПИСОК: \r- \rГРУППА АВТОРОВ 1\r(ПО УМОЛЧАНИЮ); \r- \rГРУППА АВТОРОВ 2\r. \rДОБАВЛЯЕМ ТРЕХ ПОЛЬЗОВАТЕЛЕЙ – \rTEST1\r, \rTEST2\r, \rTEST3\r. \rПОЛЬЗОВАТЕЛЯММ TEST1, TEST2 И ADMIN УСТАНАВЛИВАЕМ СОЗДАННОЕ НАМИ ПОЛЬЗОВАТЕЛЬСКОЕ ПОЛЕ (ВО ВКЛАДКЕ – ДОП. ПОЛЯ) – ГРУППА АВТОРОВ 1, А ПОЛЬЗОВАТЕЛЮ TEST3 - ГРУППА АВТОРОВ 2. \rВ ИБ НОВОСТИ СОЗДАЕМ СВОЙСТВО \rАВТОР\r: \rТИП: \rПРИВЯЗКА К ПОЛЬЗОВАТЕЛЮ\r; \rМНОЖЕСТВЕННОЕ: \rДА\r; \rКОД: \rAUTHOR_CODE\r. \rВ ИБ НОВОСТИ ДОБАВЛЯЕМ ТРИ НОВОСТИ: \r- \rТЕСТОВАЯ НОВОСТЬ №1, АВТОР – TEST3\r; \r- \rТЕСТОВАЯ НОВОСТЬ №2, АВТОР – TEST2, TEST3\r; \r- \rТЕСТОВАЯ НОВОСТЬ №3, АВТОР – ADMIN, TEST2\r. \rСУЩЕСТВУЮЩИМ НОВОСТЯМ ПРИСВАИВАЕМ СЛЕДУЮЩИХ АВТОРОВ: \r- ПОЛУЧЕНО ПРОЧНОЕ ВОДОСТОЙКОЕ СОЕДИНЕНИЕ – ADMIN; \r- МЕБЕЛЬНЫЙ ФОРУМ БЕЛАРУСИ – TEST1; \r- МЕЖДУНАРОДНАЯ МЕБЕЛЬНАЯ ВЫСТАВКА SALON DEL MOBILE – TEST1, TEST2.\rНАСТРАИВАЕМ ПАРАМЕТРЫ\r: \rID ИНФОБЛОКА С НОВОСТЯМИ – \r1\r; \rКОД СВОЙСТВА ИНФОРМАЦИОННОГО БЛОКА, В КОТОРОМ ХРАНИТСЯ АВТОР – \rAUTHOR_CODE\r; \rКОД ПОЛЬЗОВАТЕЛЬСКОГО СВОЙСТВА, В КОТОРОМ ХРАНИТСЯ ТИП АВТОРА - \rUF_AUTHOR_TYPE\r.\nСОБСТВЕННО, ВЫВОД КОМПОНЕНТА:\r\n'),
(260, 'ee376cd7842caa56dac2223f7c3f18cb', 'EX2-100\r\n[EX2-100] ДОБАВИТЬ ПУНКТ «ИБ В АДМИНКЕ» В ВЫПАДАЮЩЕМ МЕНЮ КОМПОНЕНТА \r[EX2-100] ДОБАВИТЬ ПУНКТ «ИБ В АДМИНКЕ» В ВЫПАДАЮЩЕМ МЕНЮ КОМПОНЕНТА\r\n'),
(261, '7c05ff09b9b51606e20459b2e7d8b655', 'EX2-102\r\n[EX2-102] ДОБАВИТЬ АНГЛОЯЗЫЧНУЮ ВЕРСИЮ САЙТА (ОДИН ИНФОБЛОК)\r\n'),
(262, 'a0bb36ffda5b686c7743bc7fcb0d1b5a', 'EX2-104\r\n[EX2-104] СБОР ЖАЛОБ НА НОВОСТИ, НА AJAX \rСОЗДАЕМ ИБ \r«ЖАЛОБЫ НА НОВОСТИ»\rС ПОЛЬЗОВАТЕЛЬСКИМИ СВОЙСТВАМИ: \rПОЛЬЗОВАТЕЛЬ (СТРОКА, КОД - \rUSER_CODE\r);\nНОВОСТЬ (ПРИВЯЗКА К ЭЛЕМЕНТАМ\r(НОВОСТИ, НОВОСТИ)\r, КОД - \rNEWS_CODE\r). \rСОЗДАЕМ ЭЛЕМЕНТ ИБ «ЖАЛОБЫ НА НОВОСТИ», НАПРИМЕР, ЖАЛОБА 1. НАЧАЛО АКТИВНОСТИ – ТЕКУЩАЯ ДАТА/ВРЕМЯ, НОВОСТЬ – ЛЮБАЯ, ПОЛЬЗОВАТЕЛЬ – ЛЮБОЙ. \rНАСТРОИТЬ ОТОБРАЖЕНИЕ СПИСКА ИНФОБЛОКА В АДМИНИСТРАТИВНОМ РАЗДЕЛЕ, ОТОБРАЖАТЬ КОЛОНКИ: ID, НАЧАЛО АКТИВНОСТИ, ПОЛЬЗОВАТЕЛЬ, НОВОСТЬ. \r \rЕСЛИ НЕТ /LOCAL/TEMPLATES/FURNITURE_PALE-BLUE/COMPONENTS/BITRIX/NEWS/, ТО КОПИРУЕМ ИЗ /WWW/BITRIX/COMPONENTS/BITRIX/NEWS/TEMPLATES/ И ПАПКУ .DEFAULT (КОТОРУЮ ПЕРЕИМЕНОВЫВАЕМ В EX2-104). \rВ МАССИВ $ARTEMPLATEPARAMETERS ФАЙЛА .PARAMETERS.PHP ПАРАМЕТР: \r \"REPORT_AJAX\" => ARRAY( \r \"NAME\" => GETMESSAGE(\"REPORT_AJAX\"), \r \"TYPE\" => \"CHECKBOX\", \r \"DEFAULT\" => \"N\", \r ), \rКОПИРУЕМ ТАК ЖЕ ЯЗЫКОВОЙ ФАЙЛ .PARAMETERS.PHP И ДОБАВЛЯЕМ В НЕГО \r$MESS[\'REPORT_AJAX\'] = \"СОБИРАТЬ ЖАЛОБЫ В РЕЖИМЕ AJAX\"; \rСОЗДАЕМ ФАЙЛ: /LOCAL/TEMPLATES/FURNITURE_PALE-BLUE/COMPONENTS/BITRIX/NEWS/.DEFAULT/BITRIX/NEWS.DETAIL/.DEFAULT/COMPONENT_EPILOG.PHP \rВ /LOCAL/TEMPLATES/FURNITURE_PALE-BLUE/COMPONENTS/BITRIX/NEWS/.DEFAULT/BITRIX/NEWS.DETAIL/.DEFAULT/TEMPLATE.PHP МЕЖДУ ТЕГАМИ H3 (\r<H3><?=$ARRESULT[\"NAME\"]?>\rСЮДА!\r</H3>\r) ВСТАВЛЯЕМ КОД (СМ. КОММЕНТЫ). \r \rВ ФАЙЛ /LOCAL/TEMPLATES/FURNITURE_PALE-BLUE/COMPONENTS/BITRIX/NEWS/.DEFAULT/DETAIL.PHP В МАССИВ ПОДКЛЮЧЕНИЯ КОМПОНЕНТА \"BITRIX:NEWS.DETAIL\" ДОБАВИМ ПАРАМЕТР: \r\"REPORT_AJAX\" => $ARPARAMS[\'REPORT_AJAX\'] \rСОБСТВЕННО, САМ КОМПОНЕНТ:\r\n'),
(263, 'ca54a9d2ed923a3bbd1727b2a0a9e334', 'EX2-107\r\n[EX2-107] АВТОМАТИЧЕСКИЙ СБРОС КЕША В КОМПОНЕНТЕ ПРИ ИЗМЕНЕНИИ ЭЛЕМЕНТА ИНФОРМАЦИОННОГО БЛОКА «УСЛУГИ» \rВ /WWW/BITRIX/PHP_INTERFACE/DBCONN.PHP ДОБАВЛЯЕМ КОНСТАНТУ: \rDEFINE(\"BX_COMP_MANAGED_CACHE\", TRUE); \rВ TEMPLATE.PHP ДОБАВИМ: \rМЕТКА ВРЕМЕНИ:<?ECHO TIME();?><BR> \rНУ И В COMPONENT.PHP КОЕ-ЧТО ДОБАВИЛИ...\rСОБСТВЕННО, ВЫВОД КОМПОНЕНТА:\r\n'),
(264, '34b4e1871d188a575fd715e09da25682', 'EX2-108\r\n[EX2-108] LINK REL=\"CANONICAL\" ДЛЯ ДЕТАЛЬНОЙ НОВОСТИ \rСОЗДАЕМ ИБ ТИПА НОВОСТИ: \r - СИМВОЛЬНЫЙ КОД – \rCANONICAL\r; \r - НАЗВАНИЕ – \rCANONICAL\r; \r - ДОСТУП, ДЛЯ ВСЕХ ПОЛЬЗОВАТЕЛЕЙ – \rЧТЕНИЕ\r. \rСОЗДАЕМ СВОЙСТВО ИБ С НАЗВАНИЕМ \rНОВОСТЬ\r; \r - ТИП – \rПРИВЯЗКА К ЭЛЕМЕНТАМ;\r \r- КОД: \rNEWS_MY;\r - ИНФОРМАЦИОННЫЙ БЛОК: \rНОВОСТИ\r, ТИП – \rНОВОСТИ\r. \rОТКРЫВАЕМ ИБ «CANONICAL» И ДОБАВЛЯЕМ ЭЛЕМЕНТ С НАЗВАНИЕМ:\rHTTP://TEST.RU/TEST/\r, \rНОВОСТЬ: \r[2] МЕЖДУНАРОДНАЯ МЕБЕЛЬНАЯ ВЫСТАВКА SALON DEL MOBILE\r. \rЕСЛИ НЕ СКОПИРОВАНЫ:\r КОПИРУЕМ ШАБЛОН FURNITURE_PALE-BLUE В ПАПКУ LOCAL. \r КОПИРУЕМ ПАПКУ .DEFAULT ИЗ /WWW/BITRIX/COMPONENTS/BITRIX/NEWS/TEMPLATES/ В /LOCAL/TEMPLATES/FURNITURE_PALE-BLUE/COMPONENTS/BITRIX/NEWS/. \rВ /WWW/LOCAL/TEMPLATES/FURNITURE_PALE-BLUE/COMPONENTS/BITRIX/NEWS/.DEFAULT/.PARAMETERS.PHP ДОБАВЛЯЕМ: \r\"CANONICAL\" => ARRAY( \r \"NAME\" => GETMESSAGE(\"CANONICAL\"), \r), \rВ ФАЙЛЕ .PARAMETERS.PHP ПАПКИ LANG/RU ДОБАВЛЯЕМ ПАРАМЕТР: \r$MESS[\"CANONICAL\"] = \"ID ИНФОРМАЦИОННОГО БЛОКА ДЛЯ REL=CANONICAL\";\rВ /WWW/LOCAL/TEMPLATES/FURNITURE_PALE-BLUE/COMPONENTS/BITRIX/NEWS/.DEFAULT/DETAIL.PHP В МАССИВ ВЫЗОВА КОМПОНЕНТА BITRIX:NEWS.DETAIL ДОБАВЛЯЕМ (ПОСЛЕ \"IBLOCK_URL\"): \r\"CANONICAL\" => $ARPARAMS[\"CANONICAL\"] \rВ /WWW/LOCAL/TEMPLATES/FURNITURE_PALE-BLUE/HEADER.PHP ДОБАВЛЯЕМ: \r<?$APPLICATION->SHOWPROPERTY(\"CANONICAL\");?> \rСОЗДАЕМ RESULT_MODIFIER.PHP В /WWW/LOCAL/TEMPLATES/FURNITURE_PALE-BLUE/COMPONENTS/BITRIX/NEWS/.DEFAULT/BITRIX/NEWS.DETAIL/.DEFAULT/RESULT_MODIFIER.PHP С СОДЕРЖИМЫМ: \rIF($ARPARAMS[\'CANONICAL\']){ \r $ARFILTER = ARRAY(\'IBLOCK_ID\'=>$ARPARAMS[\'CANONICAL\'],\'PROPERTY_ NEWS_MY\' => $ARRESULT[\'ID\']); \r $ARSELECT = ARRAY(\'ID\',\'IBLOCK_ID\',\'NAME\',\'PROPERTY_ NEWS_MY\'); \r $R = CIBLOCKELEMENT::GETLIST(ARRAY(),$ARFILTER,FALSE,FALSE,$ARSELECT); \r IF($RES = $R -> FETCH()){ \r $ARRESULT[\'CANONICAL\'] = $RES; \r } \r $THIS->__COMPONENT->SETRESULTCACHEKEYS(ARRAY(\'CANONICAL\')); \r} \rСОЗДАЕМ COMPONENT_EPILOG.PHP С СОДЕРЖИМЫМ: \rIF(ISSET($ARPARAMS[\'CANONICAL\'])){ \r $APPLICATION->SETPAGEPROPERTY(\"CANONICAL\", $ARRESULT[\'CANONICAL\'][\'NAME\']); \r}\r\n'),
